<?phpclass LowException extends CustomException{}class PostDataFailure extends LowException {    public function __construct($message=null) {        $message=is_null($message)?"POST Data required was not sent/invalid":$message;//default message        parent::__construct($message);    }}class AuthFailure extends LowException {    public function __construct($message="Authorization failed - Access Denied") {        Authentication::logout();        parent::__construct($message,AUTH_FAILED);    }}class SQLFailure extends LowException {    public function __construct(PDOStatement $statement=null,$message=null) {        $message=is_null($message)?"An SQL Error has occurred":$message;//default message        $code=0;        if (is_object($statement)) {            $sqlErrorInfo=$statement->errorInfo();            $code=$sqlErrorInfo[1];            if (1) $message.=" (".implode('-',$sqlErrorInfo).")";//TODO: DEBUGGING        }        parent::__construct($message,$code);    }}class NoResultFailure extends LowException {    public function __construct($message=null) {        $message=is_null($message)?"Query returned no results":$message;//default message        parent::__construct($message,R_NO_RESULTS);    }}class NotificationFailure extends LowException {    public function __construct($message=null) {        $message=is_null($message)?"Failed to send notifications":$message;//default message        parent::__construct($message);    }}class DuplicateEntry extends LowException {    public function __construct($message=null) {        $message=is_null($message)?"Entry already exists":$message;//default message        parent::__construct($message);    }}class LoginFailure extends LowException {    public function __construct($message=null) {        $message=is_null($message)?"Failed to login":$message;//default message        parent::__construct($message);    }}class RegisterFailure extends LowException {    public function __construct($message=null) {        $message=is_null($message)?"Failed to register":$message;//default message        parent::__construct($message);    }}class CookieNotSet extends LowException {    public function __construct($message=null) {        $message=is_null($message)?"Cookie not set":$message;//default message        parent::__construct($message);    }}class InvalidEntry extends LowException {    public function __construct($message=null) {        $message=is_null($message)?"Invalid entry specified":$message;//default message        parent::__construct($message);    }}class FileNotExist extends LowException {    public function __construct($message=null) {        $message=is_null($message)?"Could not find file":$message;//default message        parent::__construct($message);    }}